const appStates = new Map();

export const appState = {
    saveState(windowId, state) {
        appStates.set(windowId, state);
    },

    getState(windowId) {
        return appStates.get(windowId);
    },

    delState(windowId) {
        appStates.delete(windowId);
    },

    clearStates() {
        appStates.clear();
    }
}
