import { defineAsyncComponent } from 'vue';

// Кэш загруженных компонентов
const componentCache = new Map();

// Загрузчик компонентов с обработкой ошибок
export const loadComponent = async (appName) => {
  const { appConfig } = await import('@/config/applications');
  const app = appConfig.getAppByName(appName);

  if (!app || !app.componentPath) {
    console.error(`Приложение "${appName}" не найдено в конфигурации`);
    return null;
  }

  // Проверяем кэш
  if (componentCache.has(app.componentPath)) {
    return componentCache.get(app.componentPath);
  }

  try {
    // Создаем асинхронный компонент с обработчиками состояний
    const asyncComponent = defineAsyncComponent({
      loader: app.asyncImport,
      loadingComponent: {
        template: '<div class="app-loading">Загрузка приложения...</div>'
      },
      errorComponent: {
        template: '<div class="app-error">Ошибка загрузки приложения</div>',
        props: ['error']
      },
      delay: 200,
      timeout: 10000
    });

    // Кэшируем компонент
    componentCache.set(app.componentPath, asyncComponent);

    return asyncComponent;
  } catch (error) {
    console.error(`Ошибка загрузки компонента "${app.componentPath}":`, error);
    return null;
  }
};

// Предзагрузка приложений (опционально)
export const preloadApps = async (appNames = []) => {
  const appsToLoad = appNames.length > 0 
    ? appNames 
    : (await import('@/config/applications')).appConfig.getAllApps().map(app => app.name);

  const promises = appsToLoad.map(appName => loadComponent(appName));
  await Promise.allSettled(promises);
};
