import { LangPack } from './lang';
import { mapStores } from 'pinia';
import { useAppsStore } from '@/stores/apps.store';
import { usersTable } from '@/idb/db';
export default {
  name: 'OSICalendar',
  props: {
    windowId: {
      type: String,
      required: true
    },
    USERID: {
      type: Number,
      default: 0
    },
    selectedYear: {
      type: Number,
      default: null
    },
    selectedMonth: {
      type: Number,
      default: null
    },
    selectedDay: {
      type: Number,
      default: null
    }
  },
  data() {
    return {
      USER: null,
      lang_data: {},
      timer: null,
      currentDate: new Date(),
      currentDateStr: null,
      currentTime: null,
      selectDate: new Date()
    };
  },
  computed: {
    ...mapStores(useAppsStore),
    selectYear() {
      const targetDate = this.selectDate;
      return targetDate.getYear();
    },
    calendarWeeks() {}
  },
  methods: {
    async reloadTime() {
      this.updateTime();
      this.timer = setInterval(this.updateTime, 1000);
    },
    updateTime() {
      const now = new Date();
      this.currentDate = now;
      this.currentDateStr = now.toLocaleDateString();
      this.currentTime = now.toLocaleTimeString('ru-RU', {
        hour: '2-digit',
        minute: '2-digit'
      });
    },
    async findUser() {
      try {
        this.USER = await usersTable.getbyId(this.USERID);
      } catch (error) {
        console.error('Ошибка поиска пользователя:', error);
        this.$toast.error('Не удалось загрузить данные пользователя');
      }
    },
    // Сохраняем текущее состояние в store
    saveState() {
      if (!this.windowId || !this.appsStore || !this.isInitialized) return;
      const state = {
        appType: 'calendar',
        timestamp: Date.now()
      };
      console.log('OSICalculator saving state:', state);
      this.appsStore.saveWindowState(this.windowId, state);
    },
    // поиск по lang файлу
    LangData(key) {
      return this.lang_data[key] || '';
    },
    Get_monthStr(inpVal) {
      let monthsList = [this.LangData('january'), this.LangData('february'), this.LangData('march'), this.LangData('april'), this.LangData('may'), this.LangData('june'), this.LangData('july'), this.LangData('august'), this.LangData('september'), this.LangData('october'), this.LangData('november'), this.LangData('december')];
      return monthsList[inpVal];
    }
  },
  mounted() {
    console.log('OSICalendar app mounted with windowId:', this.windowId);
    const sDate = this.selectedYear && this.selectedMonth && this.selectedDay ? new Date(this.selectedYear, this.selectedMonth, this.selectedDay) : this.currentDate;
    this.selectDate = sDate;
    const userLang = navigator.language || navigator.userLanguage;
    const userLangS = userLang.split('-')[0];
    this.UserLang = userLangS;
    const LangPackData = LangPack;
    this.lang_data = userLangS && LangPackData && LangPackData[userLangS] ? LangPackData[userLangS] : LangPackData.en;

    // Сохраняем начальное состояние после небольшой задержки
    setTimeout(() => {
      this.saveState();
    }, 100);
    this.reloadTime();
  },
  beforeUnmount() {
    if (this.timer) {
      clearInterval(this.timer);
    }
  }
};
