import { appsConfig } from '@/config/applications';
export default {
  name: 'OSIAppManager',
  props: {
    windowId: {
      type: String,
      required: true
    },
    USERID: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      appsList: appsConfig.getAllApps(),
      searchInputTxt: ''
    };
  },
  computed: {
    filtered_appsList() {
      if (!this.searchInputTxt.trim()) {
        return this.appsList;
      } else {
        const upSearchTxt = this.searchInputTxt.toLowerCase().trim();
        return this.appsList.filter(app => {
          return app.name.toLowerCase().includes(upSearchTxt);
        });
      }
    }
  },
  methods: {
    sortByType_appsList() {
      const resultObj = {};
      const apps = this.filtered_appsList;
      // const apps = this.appsList;

      apps.forEach(element => {
        const category = element.category;
        if (!resultObj[category]) {
          resultObj[category] = [];
        }
        resultObj[category].push(element);
      });
      Object.keys(resultObj).forEach(key => {
        resultObj[key].sort((a, b) => a.label - b.label);
      });
      return resultObj;
    }
  }
};
