// useApplications.js - это composable
import { appsConfig } from '@/config/applications';

// Функция useApplications - composable
export function useApplications() {
    // Внутри можно использовать Composition API
    const getAppComponent = (appId) => {
        const app = appsConfig.getAppById(appId);
        return app ? app.asyncImport : null;
    };

    const getAppConfig = (appId) => {
        return appsConfig.getAppById(appId);
    };

    // Возвращаем методы, которые можно использовать в компонентах
    return {
        getAppComponent,
        getAppConfig,
        getAllApps: appsConfig.getAllApps,
        appsConfig
    };
}
