import { appsConfig } from '@/config/applications';
export default {
  name: "DesktopArea",
  props: {
    USERID: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      apps: [],
      bgWallpapper: null,
      USERApps: [],
      USER: null,
