import { defineStore } from 'pinia'
import { ref } from 'vue'

export const useOsStore = defineStore('os', () => {
  const windows = ref([])
  const activeWindowId = ref(null)
  let nextZIndex = 100 // Счетчик для z-index

  const openWindow = (appName) => {
    const newWindow = {
      id: Date.now().toString(),
      appName: appName,
      title: `${appName} - Приложение`,
      isMinimized: false,
      isMaximized: false,
      zIndex: nextZIndex++ // Присваиваем z-index
    }

    windows.value.push(newWindow)
    activeWindowId.value = newWindow.id

    return newWindow.id
  }

  const closeWindow = (windowId) => {
    const index = windows.value.findIndex(w => w.id === windowId)
    if (index !== -1) {
      windows.value.splice(index, 1)
    }
  }

  const minimizeWindow = (windowId) => {
    const window = windows.value.find(w => w.id === windowId)
    if (window) {
      window.isMinimized = true
    }
  }

  const restoreWindow = (windowId) => {
    const window = windows.value.find(w => w.id === windowId)
    if (window) {
      window.isMinimized = false
      this.activateWindow(windowId) // Используем активацию при восстановлении
    }
  }

  const toggleMaximizeWindow = (windowId) => {
    const window = windows.value.find(w => w.id === windowId)
    if (window) {
      window.isMaximized = !window.isMaximized
      this.activateWindow(windowId) // Используем активацию при максимизации
    }
  }

  // НОВАЯ ФУНКЦИЯ: Активация окна с обновлением z-index
  const activateWindow = (windowId) => {
    const window = windows.value.find(w => w.id === windowId)
    if (window) {
      // Устанавливаем активное окно
      activeWindowId.value = windowId

      // Обновляем z-index для всех окон
      windows.value.forEach(w => {
        if (w.id === windowId) {
          // Активному окну даем самый высокий z-index
          w.zIndex = nextZIndex++
        } else if (w.zIndex > window.zIndex) {
          // Остальным окнам оставляем их z-index
          // (ничего не делаем)
        }
      })
    }
  }

  return {
    windows,
    activeWindowId,
    openWindow,
    closeWindow,
    minimizeWindow,
    restoreWindow,
    toggleMaximizeWindow,
    activateWindow // Экспортируем новую функцию
  }
})
