// Минимальная версия для начала
import { defineStore } from 'pinia'
import { ref } from 'vue' // Убрали computed из импорта

export const useOsStore = defineStore('os', () => {
  const windows = ref([])
  const activeWindowId = ref(null)
  const nextZIndex = ref(100)
//   const processCounter = ref(0)

    const launchApp = (appId, title = 'Новое окно') => {
        const windowId = `win_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`

        const newWindow = {
            id: windowId,
            appId,
            title,
            isMinimized: false,
            isMaximized: false,
            zIndex: nextZIndex.value++,
            position: {
                x: 100 + windows.value.length * 30,
                y: 100 + windows.value.length * 30
            },
            size: {
                width: 600,
                height: 400
            },
            icon: '/icons/default.png'
        }

        windows.value.push(newWindow)
        activeWindowId.value = windowId

        return windowId
    }

    const closeWindow = (windowId) => {
        const index = windows.value.findIndex(w => w.id === windowId)
        if (index !== -1) {
      windows.value.splice(index, 1)
      if (activeWindowId.value === windowId) {
        activeWindowId.value = windows.value.length > 0 ? windows.value[0].id : null
      }
    }
  }

  const bringToFront = (windowId) => {
    const window = windows.value.find(w => w.id === windowId)
    if (window) {
      window.zIndex = nextZIndex.value++
      activeWindowId.value = windowId
    }
  }

  return {
    windows,
    activeWindowId,
    launchApp,
    closeWindow,
    bringToFront
  }
})
