      const query = this.searchInputTxt.toLowerCase().trim();
      console.log('query', query);
      return this.appsList.filter(app => {
        // Проверяем наличие поискового запроса в названии
        return app.label.toLowerCase().includes(query);
      });
    },
    sortByType_appsList() {
      console.log('111');
      const resultObj = {};
      const apps = this.filteredApps;
      // const apps = this.appsList;

      apps.forEach(element => {
        const category = element.category;
        if (!resultObj[category]) {
          resultObj[category] = [];
        }
        resultObj[category].push(element);
      });
      Object.keys(resultObj).forEach(key => {
        resultObj[key].sort((a, b) => a.label - b.label);
      });
      return resultObj;
    }
