// os.store.js - обновленная версия
import { defineStore } from 'pinia'
import { ref } from 'vue'

export const useNotifStore = defineStore('os', () => {
    const LOCALSTORAGE_KEY = "osi_notifs";
    const notifs = ref([]);
    notifs.value.push({appId: 'osihelper', content: 'asdasdasdasdasd', header: 'asdasdasdasd'});

    // Загружаем сохраненные уведомления из localStorage при инициализации
    const loadNotifsFromStorage = () => {
        try {
            const saved = localStorage.getItem(LOCALSTORAGE_KEY);

            if (saved) {
                const parsed = JSON.parse(saved);

                if (parsed && Array.isArray(parsed)) {
                    notifs.value = parsed;
                    console.log('Loaded notifs from storage:', notifs.value.length);
                }
            }
        } catch (error) {
            console.error('Failed to load notifs from storage:', error);
        }
    };

    // Сохраняем уведомления в localStorage
    const saveNotifsToStorage = () => {
        try {
            const state = notifs.value.map(w => ({
                    appId: w.appId,
                    content: w.content,
                    header: w.header,
                }));

            localStorage.setItem(LOCALSTORAGE_KEY, JSON.stringify(state));
        } catch (error) {
            console.error('Failed to save notifs to storage:', error);
        }
    };



    // Инициализация
    loadNotifsFromStorage();

    // Метод для очистки всех уведомлений
    const clearAllNotifs = () => {
        notifs.value = [];
        localStorage.removeItem(LOCALSTORAGE_KEY);
    };

    return {
        notifs,
        clearAllNotifs,
        saveNotifsToStorage
    };
});
