export default {
  name: "SimpleWindow",
  props: {
    windowId: String,
    title: {
      type: String,
      default: '–û–∫–Ω–æ'
    },
    appName: {
      type: String,
      default: 'app'
    },
    isMinimized: {
      type: Boolean,
      default: false
    },
    isActive: {
      type: Boolean,
      default: false
    },
    isMaximized: {
      type: Boolean,
      default: false
    },
    zIndex: {
      type: Number,
      default: 100
    }
  },
  emits: ['close', 'minimize', 'focus', 'toggleMaximize'],
  data() {
    return {
      posX: 100,
      posY: 100,
      windowWidth: 400,
      windowHeight: 300,
      isDragging: false,
      isResizing: false,
      dragOffset: {
        x: 0,
        y: 0
      },
      resizeDirection: '',
      resizeStart: {
        x: 0,
        y: 0,
        width: 0,
        height: 0,
        mouseX: 0,
        mouseY: 0
      }
    };
  },
  computed: {
    windowStyles() {
      console.log('üîß –í—ã—á–∏—Å–ª—è—é —Å—Ç–∏–ª–∏ –¥–ª—è –æ–∫–Ω–∞:', this.windowId);
      const baseStyles = {
        zIndex: this.zIndex || 100,
        backgroundColor: 'blue',
        // –í—Ä–µ–º–µ–Ω–Ω—ã–π —Ü–≤–µ—Ç –¥–ª—è —Ç–µ—Å—Ç–∞
        border: '5px solid yellow'
      };
      if (this.isMaximized) {
        console.log('üîÑ –û–∫–Ω–æ –º–∞–∫—Å–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–æ');
        return {
          ...baseStyles,
          left: '0',
          top: '0',
          width: '100vw',
          height: '100vh',
          borderRadius: '0'
        };
      } else {
        console.log('üìê –û–±—ã—á–Ω–æ–µ –æ–∫–Ω–æ, –ø–æ–∑–∏—Ü–∏—è:', this.posX, this.posY);
        return {
          ...baseStyles,
          left: `${this.posX}px`,
          top: `${this.posY}px`,
          width: `${this.windowWidth}px`,
          height: `${this.windowHeight}px`
        };
      }
    }
  },
  mounted() {
    document.addEventListener('mousemove', this.handleMouseMove);
    document.addEventListener('mouseup', this.handleMouseUp);
  },
  beforeUnmount() {
    document.removeEventListener('mousemove', this.handleMouseMove);
    document.removeEventListener('mouseup', this.handleMouseUp);
  },
  methods: {
    // –ê–∫—Ç–∏–≤–∞—Ü–∏—è –æ–∫–Ω–∞ –ø—Ä–∏ –∫–ª–∏–∫–µ
    activateWindow() {
      this.$emit('focus', this.windowId); // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–æ–±—ã—Ç–∏–µ
    },
    // –ù–∞—á–∞–ª–æ –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–Ω–∏—è
    startDrag(e) {
      // –°–Ω–∞—á–∞–ª–∞ –∞–∫—Ç–∏–≤–∏—Ä—É–µ–º –æ–∫–Ω–æ
      this.$emit('focus', this.windowId);

      // –ï—Å–ª–∏ –æ–∫–Ω–æ –º–∞–∫—Å–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–æ - –Ω–µ –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–µ–º
      if (this.isMaximized) {
        return;
      } else {
        // –ù–∞—á–∏–Ω–∞–µ–º –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–Ω–∏–µ
        this.isDragging = true;
        this.dragOffset = {
          x: e.clientX - this.posX,
          y: e.clientY - this.posY
        };
        e.stopPropagation();
      }
    },
    // –ù–∞—á–∞–ª–æ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞
    startResize(direction) {
      if (this.isMaximized) return;

      // –ê–∫—Ç–∏–≤–∏—Ä—É–µ–º –æ–∫–Ω–æ
      this.$emit('focus', this.windowId);
      this.isResizing = true;
      this.resizeDirection = direction;

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–∞—á–∞–ª—å–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
      this.resizeStart.x = this.posX;
      this.resizeStart.y = this.posY;
      this.resizeStart.width = this.windowWidth;
      this.resizeStart.height = this.windowHeight;
      this.resizeStart.mouseX = event.clientX;
      this.resizeStart.mouseY = event.clientY;
    },
    // –û–±—Ä–∞–±–æ—Ç–∫–∞ –¥–≤–∏–∂–µ–Ω–∏—è –º—ã—à–∏
    handleMouseMove(e) {
      if (this.isDragging && !this.isMaximized) {
        // –ü–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–Ω–∏–µ –æ–∫–Ω–∞
        let newX = e.clientX - this.dragOffset.x;
        let newY = e.clientY - this.dragOffset.y;

        // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –ø–æ–∑–∏—Ü–∏—é —ç–∫—Ä–∞–Ω–æ–º
        const maxX = window.innerWidth - this.windowWidth;
        const maxY = window.innerHeight - this.windowHeight;
        this.posX = Math.max(0, Math.min(newX, maxX));
        this.posY = Math.max(0, Math.min(newY, maxY));
      } else if (this.isResizing && !this.isMaximized) {
        // –ò–∑–º–µ–Ω–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
        this.handleResize(e);
      }
    },
    // –ò–∑–º–µ–Ω–µ–Ω–∏–µ —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
    handleResize(e) {
      const deltaX = e.clientX - this.resizeStart.mouseX;
      const deltaY = e.clientY - this.resizeStart.mouseY;

      // –ú–∏–Ω–∏–º–∞–ª—å–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã –æ–∫–Ω–∞
      const minWidth = 200;
      const minHeight = 150;

      // –ù–∞—á–∞–ª—å–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
      let newX = this.resizeStart.x;
      let newY = this.resizeStart.y;
      let newWidth = this.resizeStart.width;
      let newHeight = this.resizeStart.height;

      // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏—è
      if (this.resizeDirection.includes('e')) {
        // –ò–∑–º–µ–Ω–µ–Ω–∏–µ –ø—Ä–∞–≤–æ–π –≥—Ä–∞–Ω–∏—Ü—ã
        newWidth = Math.max(minWidth, this.resizeStart.width + deltaX);
      }
      if (this.resizeDirection.includes('w')) {
        // –ò–∑–º–µ–Ω–µ–Ω–∏–µ –ª–µ–≤–æ–π –≥—Ä–∞–Ω–∏—Ü—ã
        newWidth = Math.max(minWidth, this.resizeStart.width - deltaX);
        newX = this.resizeStart.x + (this.resizeStart.width - newWidth);
      }
      if (this.resizeDirection.includes('s')) {
        // –ò–∑–º–µ–Ω–µ–Ω–∏–µ –Ω–∏–∂–Ω–µ–π –≥—Ä–∞–Ω–∏—Ü—ã
        newHeight = Math.max(minHeight, this.resizeStart.height + deltaY);
      }
      if (this.resizeDirection.includes('n')) {
        // –ò–∑–º–µ–Ω–µ–Ω–∏–µ –≤–µ—Ä—Ö–Ω–µ–π –≥—Ä–∞–Ω–∏—Ü—ã
        newHeight = Math.max(minHeight, this.resizeStart.height - deltaY);
        newY = this.resizeStart.y + (this.resizeStart.height - newHeight);
      }

      // –ü—Ä–∏–º–µ–Ω—è–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏—è
      this.posX = newX;
      this.posY = newY;
      this.windowWidth = newWidth;
      this.windowHeight = newHeight;

      // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –æ–∫–Ω–æ –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö —ç–∫—Ä–∞–Ω–∞
      this.keepWindowInBounds();
    },
    // –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ –æ–∫–Ω–∞ –≤ –ø—Ä–µ–¥–µ–ª–∞—Ö —ç–∫—Ä–∞–Ω–∞
    keepWindowInBounds() {
      const minWidth = 200;
      const minHeight = 150;
      const screenWidth = window.innerWidth;
      const screenHeight = window.innerHeight;

      // –ì–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ–º –º–∏–Ω–∏–º–∞–ª—å–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã
      this.windowWidth = Math.max(minWidth, this.windowWidth);
      this.windowHeight = Math.max(minHeight, this.windowHeight);

      // –ù–µ –¥–∞–µ–º –æ–∫–Ω—É –≤—ã–π—Ç–∏ –∑–∞ –ø—Ä–∞–≤—É—é –≥—Ä–∞–Ω–∏—Ü—É
      if (this.posX + this.windowWidth > screenWidth) {
        this.windowWidth = screenWidth - this.posX;
      }

      // –ù–µ –¥–∞–µ–º –æ–∫–Ω—É –≤—ã–π—Ç–∏ –∑–∞ –Ω–∏–∂–Ω—é—é –≥—Ä–∞–Ω–∏—Ü—É
      if (this.posY + this.windowHeight > screenHeight) {
        this.windowHeight = screenHeight - this.posY;
      }

      // –ù–µ –¥–∞–µ–º —É–π—Ç–∏ –∑–∞ –ª–µ–≤—É—é –∏ –≤–µ—Ä—Ö–Ω—é—é –≥—Ä–∞–Ω–∏—Ü—ã
      this.posX = Math.max(0, this.posX);
      this.posY = Math.max(0, this.posY);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –º–∏–Ω–∏–º–∞–ª—å–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã –µ—â–µ —Ä–∞–∑
      this.windowWidth = Math.max(minWidth, this.windowWidth);
      this.windowHeight = Math.max(minHeight, this.windowHeight);
    },
    // –û–∫–æ–Ω—á–∞–Ω–∏–µ –ø–µ—Ä–µ—Ç–∞—Å–∫–∏–≤–∞–Ω–∏—è/–∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞
    handleMouseUp() {
      if (this.isDragging || this.isResizing) {
        // –§–∏–Ω–∞–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ –≥—Ä–∞–Ω–∏—Ü
        this.keepWindowInBounds();
      }
      this.isDragging = false;
      this.isResizing = false;
    },
    // –°–≤–æ—Ä–∞—á–∏–≤–∞–Ω–∏–µ –æ–∫–Ω–∞
    minimize() {
      this.$emit('minimize', this.windowId);
    },
    // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –º–∞–∫—Å–∏–º–∏–∑–∞—Ü–∏–∏
    toggleMaximize() {
      this.$emit('toggleMaximize', this.windowId);
    },
    // –ó–∞–∫—Ä—ã—Ç–∏–µ –æ–∫–Ω–∞
    close() {
      this.$emit('close', this.windowId);
    }
  }
};
